var fs = require('fs');


module.exports.loadROM = function(filename)
{
	var data = fs.readFileSync(filename);
	
	var x = parseInt('0x200');

	var ROM =
	{
		"address" : '0x200', /* The first 512 bytes are reserved for interpreter */
		"size" : data.length.toString(),
		"data" : data.toString('base64', 0, data.length)
	};

	return ROM;
}
